'use strict';

module.exports = require('./lib/express');
const express = require('express');
const app = express();
const port = 3000; // o cualquier otro puerto que desees utilizar

// Endpoint para la suma de dos números
app.get('/operacion', (req, res) => {
    const num1 = parseFloat(req.query.num1);
    const num2 = parseFloat(req.query.num2);

    if (isNaN(num1) || isNaN(num2)) {
        return res.status(400).json({ error: 'Los números deben ser válidos' });
    }

    const resultado = num1 + num2;
    return res.json({ resultado });
});

// Iniciar el servidor
app.listen(port, () => {
    console.log(`Servidor en funcionamiento en http://localhost:8765/operacion/5/10/suma${port}`);
});

